        $char = DB::connection('characters')
            ->table('characters')
            ->select('guid', 'name', 'race', 'class', 'gender', 'level', 'health', 'mana', 'activespec', 'chosenTitle', 'logout_time')
            ->where('name', Str::ucfirst($characters))
            ->firstOrFail();

