        $lastLogin = strtotime(Text::lastLoginCharacters($char->logout_time));
        $days_between = ceil(abs($lastLogin - $date) / 86400);

        if($days_between >= 60.0) {
            $isOutdated = true;
        } else {
            $isOutdated = false;
        }

